%text Because \small\[ y = 2\tan^{-1} \left(\sqrt{\ea}\cdot\tan \frac{x}{2} \right) = \cos^{-1} \left(\frac{a\cos x + b}{a + b\cos x} \right)\] \normalsize Therefore % \begin{align}
\tan \frac{y}{2} &= \sqrt{\ea}\cdot\tan \frac{x}{2} \\ \text{and } \cos y &= \frac{a\cos x + b}{a + b\cos x} \end{align} %text
\incorrect Because \small\[ y = 2\tan^{-1} \left(\sqrt{\ea}\cdot\tan \frac{x}{2} \right) = \cos^{-1} \left(\frac{a\cos x + b}{a + b\cos x} \right)\] \normalsize Therefore \[ \tan \frac{y}{2} = \sqrt\ea\cdot\tan \frac{x}{2} = \cos y = \frac{a\cos x + b}{a + b\cos x}\] %

reason.tex:
%text
 This problem looks scarier than it is. So, let us do the simplest things first \small\[ y = 2\tan^{-1} \left(\sqrt{\ea}\cdot\tan \frac{x}{2} \right) = \cos^{-1} \left(\frac{a\cos x + b}{a + b\cos x} \right)\] \normalsize And hence
%
\begin{align}

y &= 2\tan^{-1} \left(\sqrt{\ea}\cdot\tan \frac{x}{2} \right) \\
\frac{y}{2} &= \tan^{-1} \left(\sqrt\ea\cdot\tan \frac{x}{2} \right) \\
\text{or } \tan \frac{x}{2} &= \sqrt\ea\cdot\tan\frac{x}{2}
\end{align}
%text
 Similarly
%
\begin{align}

y &= \cos^{-1} \left( \frac{a\cos x + b}{a + b\cos x}\right) \\
\therefore \cos y &= \frac{a\cos x + b}{a + b\cos x}
\end{align}
%text
 This \underline\text{does not} mean that $\tan \frac{y}{2} = \cos y$ (just in case you were wondering)%

options.tex:
%text

%
\begin{align}

\cos y &= \frac{1 - \tsqy}{1 + \tsqy} \\
&= \left[ \frac{1-\ea\cdot\tsqx}{1 + \ea\cdot\tsqx}\right] \\
&= \left[\frac{a\cdot \left(1-\tsqx \right) + b\cdot \left(1+\tsqx \right)}{a\cdot \left(1+\tsqx \right) + b\cdot \left(1-\tsqx \right)} \right] \\
&= \frac{a\cdot\cos x + b}{a + b\cdot \cos x}\quad\text{ (hence proved)}
\end{align}
%text
