%text From the given equations of the lines, we can infer the following % \begin{align} \begin{tabular}{NNN}
\hline \text{Line} & \text{Cuts $x-$axis at} & \text{Cuts $y-$ axis at} \\ \hline y = 2 + x & (-2,0) & (0,2) \\ \hline y = 2 - x & (2,0) & (0,2) \\ \hline x = 2 & (2,0) & \text{Never} \\ \hline \end{tabular} \end{align} %text Which gives us the following picture. The shaded region is the required area $R$ % \begin{align}
\includegraphics[scale=1.4]{figure.svg} \end{align} %text %

%text

%
\begin{align}

R &= \int_0^2 \left(x+2 \right)\cdot dx - \int_0^2 \left(2-x \right)\cdot dx\\
&= \underbrace{\int_0^2 \left(x+2 - 2 + x \right)\cdot dx }_{\int_a^b f(x) dx + \int_a^b g(x)dx = \int_a^b \left(f(x) + g(x) \right)\cdot dx} \\
&= 2 \int_0^2 x\cdot dx = 2 \left[\frac{x^2}{2} \right]_0^2 = 4\text{ sq. units}
\end{align}
